import "./PhotoProductions.css";
import { useContext, useState } from "react";
import { dataContext } from "../context/DataContext";
import { IoCloseOutline } from "react-icons/io5";
import { BiLoaderAlt } from "react-icons/bi";

const PhotoProductions = () => {
  const { photoProductions, photoProductionsResults } = useContext(dataContext);
  const [modal, setModal] = useState(false);
  console.log(photoProductions);
  const openModal = () => {
    setModal(!modal);
  };

  return photoProductions.map((videos) => {
    return (
      <ul className='photo-list-container' key={videos.id}>
        <li className='photo-list-item'>
          <img src={videos.img} />
          <div className='photo-list-item-info' onClick={openModal}>
            <h3>{videos.name} </h3>
            <h6>{videos.description}</h6>
            {modal ? (
              <section className='modal__bg'>
                <div className='modal__align'>
                  <div modal={modal}>
                    <IoCloseOutline className='modal__close' arial-label='Close modal' onClick={setModal} />
                    <div className='modal__photo-align'>
                      {videos.imageResults.map((headers) => {
                        return <img src={headers} />;
                      })}
                    </div>
                  </div>
                </div>
              </section>
            ) : null}
          </div>
        </li>
      </ul>
    );
  });
};

export default PhotoProductions;
